#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
}
$ret=0
if (Test-Path "$basedir/-u$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/-u$exe" DEBUG node "$basedir/../ja/cli/ja.js" $args
  } else {
    & "$basedir/-u$exe" DEBUG node "$basedir/../ja/cli/ja.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "-u$exe" DEBUG node "$basedir/../ja/cli/ja.js" $args
  } else {
    & "-u$exe" DEBUG node "$basedir/../ja/cli/ja.js" $args
  }
  $ret=$LASTEXITCODE
}
exit $ret
